// SPDX-License-Identifier: GPL-3.0
pragma solidity =0.6.12;

import './ISolarRouter01.sol';

interface ISolarRouter02 is ISolarRouter01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
